KotlinJava <pre><code>ColorPickerDialog.Builder(this)
    .setTitle("ColorPicker Dialog")
    .setPreferenceName("MyColorPickerDialog")
    .setPositiveButton("Confirm", ColorEnvelopeListener { envelope, fromUser -&gt;
        setLayoutColor(envelope)
    })
    .setNegativeButton("Cancel") { dialogInterface, _ -&gt;
        dialogInterface.dismiss()
    }
    .attachAlphaSlideBar(true)
    .attachBrightnessSlideBar(true)
    .setBottomSpace(12)
    .show()
</code></pre> <pre><code>new ColorPickerDialog.Builder(this)
    .setTitle("ColorPicker Dialog")
    .setPreferenceName("MyColorPickerDialog")
    .setPositiveButton(getString(R.string.confirm),
        new ColorEnvelopeListener() {
            @Override
            public void onColorSelected(ColorEnvelope envelope, boolean fromUser) {
                setLayoutColor(envelope);
            }
        })
    .setNegativeButton(getString(R.string.cancel),
        new DialogInterface.OnClickListener() {
            @Override
            public void onClick(DialogInterface dialogInterface, int i) {
                dialogInterface.dismiss();
            }
        })
    .attachAlphaSlideBar(true)
    .attachBrightnessSlideBar(true)
    .setBottomSpace(12)
    .show();
</code></pre>

KotlinJava <pre><code>class CustomFlag(context: Context, layout: Int) : FlagView(context, layout) {

    private val textView: TextView = findViewById(R.id.flag_color_code)
    private val alphaTileView: AlphaTileView = findViewById(R.id.flag_color_layout)

    override fun onRefresh(colorEnvelope: ColorEnvelope) {
        textView.text = "#${colorEnvelope.hexCode}"
        alphaTileView.setPaintColor(colorEnvelope.color)
    }
}
</code></pre> <pre><code>public class CustomFlag extends FlagView {

    private TextView textView;
    private AlphaTileView alphaTileView;

    public CustomFlag(Context context, int layout) {
        super(context, layout);
        textView = findViewById(R.id.flag_color_code);
        alphaTileView = findViewById(R.id.flag_color_layout);
    }

    @Override
    public void onRefresh(ColorEnvelope colorEnvelope) {
        textView.setText("#" + colorEnvelope.getHexCode());
        alphaTileView.setPaintColor(colorEnvelope.getColor());
    }
}
</code></pre>
